	int t = 0, i = 0;
	char TempBuff[64];
	int len = 0, len2 = 0;
	int tSetTextXposi = 0;
	int x, y;
	len = lstrlen(szInfoBuff);

	memset(&TempBuff, 0, sizeof(TempBuff));
